import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import SlideMenuClusterAdmin from "../components/component.slidemenu.clusterAdmin";
import User from "../components/component.users.display";
import Blood from "../components/component.blood.display"
import '../assests/css/page.clusterAdmin.dashboard.css'
export default function ClusterAdminDashboard() {
  const [slidemenu, setSlideMenu] = useState(true);
  const passData = (data) => {
    setSlideMenu(data);
  };
 
    const lineChartData = {
      labels: ["October", "November", "December"],
      datasets: [
        {
          data: [8137119, 9431691, 10266674],
          label: "Infected",
          borderColor: "#3333ff",
          fill: true,
          lineTension: 0.5
        },
        {
          data: [1216410, 1371390, 1477380],
          label: "Deaths",
          borderColor: "#ff3333",
          backgroundColor: "rgba(255, 0, 0, 0.5)",
          fill: true,
          lineTension: 0.5
        }
      ]
    };
 
  return (
    <div>
      <SlideMenuClusterAdmin headerName={"Dashboard"} passData={passData} />
      <div id={`${slidemenu ? 'state-change-true' : 'state-change-false'}`} >
        <div id="row-1">
          <div id="medical-staff">
            <h3>Medical Staff Members</h3>
            <div id="users-count" display='inline'> <User id='user' />  <User id='user' />  <User id='user' />  <User id='user' /> </div>
          </div>
          <div id="camp">
            <h3>Blood Camps</h3>
            <div id="camp-count" display='inline'> <User id='user' />  <User id='user' />  <User id='user' />  <User id='user' /></div>
          </div>
        </div>
        <div id="row-2">
          <div id="sub-row-2">
            <div id="blood"><Blood name='O+' /><Blood name='A+' /><Blood name='B+' /><Blood name='AB+' /></div>
            <div id="blood"><Blood name='O-' /><Blood name='A-' /><Blood name='B-' /><Blood name='AB-' /></div>
          </div>
          <Line
            type="line"
            width={160}
            height={60}
            options={{
              title: {
                display: true,
                text: "COVID-19 Cases of Last 6 Months",
                fontSize: 20
              },
              legend: {
                display: true, //Is the legend shown?
                position: "top" //Position of the legend.
              }
            }}
            data={lineChartData}
          />
        </div>
      </div>
    </div>
  );
}
