import Test from "./components/test";
function App() {
    const [loginStatus, setLoginStatus] = useState(false); 
    const type = localStorage.getItem("type");
   return (
    <BrowserRouter> 
     <div className="App">

     </div>  
        <Routes>
           <Route path='/login' exact element={ 
                     localStorage.getItem("token") ?()=>{ 
                           if(type==1){
                              <SlideMenu />
                           }else if(type==2){

                           }
                     } : <Login/>} />
           <Route path='/dashboard' exact element={ localStorage.getItem("token") ?<SlideMenu /> : <Login/>} />
           <Route path='/test' exact element={<Test/>} />

        </Routes>
      </BrowserRouter>  
);


}

export default App;
