import React, { useEffect } from 'react';
import Chart from 'chart.js/auto';
const BarApp = (props) => {
    useEffect(() => {
        var myChart = new Chart(table );
        return () => {
            myChart.destroy()
          }
        }, [])
        
        
    var table = "myChart";
    var myChart = new Chart(table, {
      type: "bar",
      data: {
        labels: [
          "Day 1",
          "Day 2",
          "Day 3",
          "Day 4",
          "Day 5",
          "Day 6",
          "Day 7",
          "Day 8",
          "Day 9",
          "Day 10",
        ],
        datasets: [
          {
            label: "no.of Taps",
            data: [32, 256, 144, 168, 248, 96, 144, 168, 40, 144],
          },
        ],
      },
  
      options: {
        
        scales: {
          x: {
  
            display: false,
            grid: {
              display: false
            }
          },
          y: {
  
            display: false, 
            grid: {
              display: false
            }
          }
        },
      }
    });
  
    return (
      <div className="insights-container p-3">
        <div className="blue-heading">No. of Taps</div>
        <div className="description">Last 10 days</div>{" "}
        <>
        <div id="chartReport">
          <canvas id="myChart"></canvas>
          </div>
        </>
      </div>
    );
  };
 
 export default BarApp;
