import {BrowserRouter, Routes, Route} from 'react-router-dom';
import Login from "./pages/page.login";
import SlideMenuAdmin from "./components/component.slidemenu.admin";
import SlideMenuClusterAdmin from "./components/component.slidemenu.clusterAdmin";
import SlideMenuMedicalOfficer from "./components/component.slidemenu.medicalOfficer";
import SlideMenuHeadNurse from "./components/component.slidemenu.headNurse";
import SlideMenuRegDonor from "./components/component.slidemenu.registerDonor";
import Test from "./components/test";
function App() {
    const [loginStatus, setLoginStatus] = useState(false); 
    const type = localStorage.getItem("type");



   return (
    <BrowserRouter> 
     <div className="App">

     </div>  
        <Routes>
         {/* Admin Routes */}
         { localStorage.getItem("type")==="1" ?  <Route path='/dashboard' exact element={ localStorage.getItem("token") ? <SlideMenuAdmin /> : <Login/>} />:  <Login/> } 
         { localStorage.getItem("type")==="1" ?  <Route path='/login' exact element={ localStorage.getItem("token") ?  <SlideMenuAdmin />: <Login/>} />:  <Login/>}


        </Routes>
      </BrowserRouter>  
);


}

export default App;
